mapper.select:
	select count(1) from brand_news_tbl 
	where 1=1 
	
    	and title like CONCAT('%', #{title}, '%')
	
	
    	and brand_id=#{brandId}
	
	
    	and brand_type=#{brandType}
	
	
    	and category=#{category}
	
	
    	and schoolType=#{schoolType}
	
	
    	and language=#{language}

	select count(1) from brand_news_tbl 
	where 1=1 
	
    	and title like CONCAT('%', #{title}, '%')
	
	
    	and brand_type=#{brandType}
	
	
    	and category=#{category}
		
	
    	and language=#{language}

		select 
		
		 ,
    
		from brand_news_tbl 
		where 1=1
			
	    	and title like CONCAT('%', #{title}, '%')
		
		
	    	and brand_id=#{brandId}
		
		
	    	and brand_type=#{brandType}
		
		
	    	and category=#{category}
		
		
	    	and schoolType=#{schoolType}
		
		
	    	and language=#{language}
		
		
	      ${pager.orderCondition}
	    
	    
	       ${pager.mysqlQueryCondition}

		select 
		
		 ,
        
		from brand_news_tbl 
		where 1=1
		
	    	and brand_id=#{brandId}
		
		
	    	and brand_type=#{brandType}
		
		
	    	and category=#{category}
		
		
	    	and schoolType=#{schoolType}
		
		
	    	and language=#{language}

	select count(1) from brand_news_tbl
	where flag = #{flag}
	
    	and brand_id=#{brandId}
	
	
    	and brand_type=#{brandType}
	
	
    	and category=#{category}
	
	
    	and schoolType=#{schoolType}
	
	
    	and language=#{language}

		select 
		 
    		,
    	
		from brand_news_tbl
		where 1=1 and flag = #{flag}
			
	    		and brand_id=#{brandId}
			
			
			 
	   		order by signal_seq desc
    		
			
			
	    		order by all_seq desc
			
			
		      ${pager.orderCondition}
		    
		    
		       ${pager.mysqlQueryCondition}

		select 
		
    	,
    	
		from brand_news_tbl  
		where flag = #{flag} 
		
	    	and brand_id=#{brandId}
		
		
	    	and brand_type=#{brandType}
		
		
	    	and category=#{category}
		
		
	    	and schoolType=#{schoolType}
		
		
	    	and language=#{language}
		
		
	    	and index_seq=#{indexSeq}
		
		
	    	and signal_seq=#{signal_seq}
		
		
	    	and (signal_seq=#{signal_seq} or signal_seq is null)
		
		order by create_time desc
		
	    	limit #{limit},#{page}

	select count(1) from brand_news_tbl
	where flag = #{flag}
	
    	and brand_id=#{brandId}
	
	
    	and brand_type=#{brandType}
	
	
    	and category=#{category}
	
	
    	and schoolType=#{schoolType}
	
	
    	and language=#{language}
	
	
    	and index_seq=#{indexSeq}
		
	
    	and signal_seq=#{signal_seq}
	
	
    	and (signal_seq=#{signal_seq} or signal_seq is null)
