mapper.select:
    select 
        T1.*
    from 
        category_tbl T1 
    where  1=1
  		and is_deleted = '0'
  		and brand_type = #{brandType,jdbcType=VARCHAR}
  	
		and language = #{language,jdbcType=VARCHAR}
	
	
		and parent_id != #{nParentid,jdbcType=VARCHAR}
	
	
		and parent_id = #{parentid,jdbcType=VARCHAR}
	
  	order by seq asc

		select count(1) from category_tbl 
		where 1=1
	       and parent_id = #{parentId}
	       and brand_type = #{brandType}
      	
			and language = #{language}

	select 
	
	from category_tbl 
	where 1=1
    and parent_id = #{params.parentId} 
    and brand_type = #{params.brandType}
    
    	and language = #{params.language}
    
    order by id asc 
	limit #{page},#{rows}

	select 
	
	from category_tbl 
	where 1=1
    and parent_id = #{parentId} 
    and brand_type = #{brandType}
    
    	and language = #{language}
